package com.basicframe.utils.page;import javax.servlet.jsp.JspException;import javax.servlet.jsp.tagext.TagSupport;import com.basicframe.utils.CommonUtil;public class FormatDate extends TagSupport {		private static final long serialVersionUID = -3281689141982775524L;	private String dateformat;	private java.util.Date timestamp;	public void setDateformat(String dateformat) {		this.dateformat = dateformat;	}	public void setTimestamp(java.util.Date timestamp) {		this.timestamp = timestamp;	}	public int doStartTag() throws JspException {		try {			pageContext.getOut().write(CommonUtil.sgmdate(dateformat, timestamp));		} catch (Exception e) {			e.printStackTrace();		}		return super.doStartTag();	}}